mapper.select:
select * from board where board_category = #{arg0}
and title like '%' || #{arg1} || '%'
order by create_date desc

mapper.update:
update board
title = #{title}, content = #{content}, update_date = sysdate
where seq_id = #{seq_id}

mapper.select:
		select count(*) from board 
		where board_category = #{arg0} 

            and title like '%' || #{arg1} || '%'
