mapper.select:
SELECT * FROM relief
WHERE relief_category = #{relief_category}

        and title like '%' || #{searchText} || '%'

  AND mbti = #{mbti}
ORDER BY create_date DESC

mapper.update:
update relief
title = #{title}, content = #{content}, update_date = sysdate
where seq_id = #{seq_id}

mapper.select:
		select count(*) from relief 
		where relief_category = #{relief_category} 

            and title like '%' || #{searchText} || '%'
