mapper.select:
SELECT * FROM relief
WHERE
relief_category = #{relief_category}
and mbti = #{mbti}
and stress_level = #{stress_level}

	and title like '%' || #{searchText} || '%'

ORDER BY create_date DESC

mapper.update:
update relief
title = #{title}, content = #{content}, update_date = sysdate
where relief_id = #{relief_id}

mapper.select:
select count(*) from relief
where relief_category = #{relief_category}
and mbti = #{mbti}
and stress_level = #{stress_level}

	and title like '%' || #{searchText} || '%'
